import os, player, sys, helpers, cli
cursor = "={=======- "

def title_screen_selections():
    valid_response = False
    while not valid_response:
        match input(cursor).lower():
            case 'play':
                display_game_options()
                valid_response = True
            case 'help':
                display_help_screen()
                valid_response = True
            case 'quit':
                sys.exit()

def game_options_selections():
    valid_response = False
    while not valid_response:
        match input(cursor).lower():
            case 'load game':
                display_save_files()
                valid_response = True
            case 'new game':
                cli.setup_game()
                valid_response = True
            case 'back':
                display_title_screen()
                valid_response = True

def save_files_selections(all_player_names):
  valid_response = False
  while not valid_response:
    match input(cursor).lower():
      case 'load':
          load_file_selections(all_player_names)
          valid_response = True
      case 'delete':
          delete_file_selections(all_player_names)
          valid_response = True
      case 'back':
          display_game_options()
          valid_response = True

def load_file_selections(all_player_names):
  valid_response = False
  while not valid_response:
    print('\nWhich file would you like to load?\n')
    response = input(cursor)
    match response:
        case player_name if player_name in all_player_names:
          global my_player
          cli.my_player = player.find_player_by_name(response)
          os.system('clear')
          # print(my_player.location.description + '\n\n')
          cli.game_loop()
          valid_response = True
        case 'back':
          display_save_files()
          valid_response = True

def delete_file_selections(all_player_names):
  valid_response = False
  while not valid_response:
    print('\nWhich file would you like to delete?\n')
    response = input(cursor)
    match response:
        case player_name if player_name in all_player_names:
          player_to_delete = player.find_player_by_name(response)
          player.delete_player(player_to_delete)
          valid_response = True
        case 'back':
          valid_response = True
  display_save_files()
          
def help_screen_selections():
   valid_response = False
   while not valid_response:
      match input(cursor).lower():
         case 'back':
            display_title_screen()
            valid_response = True

